graphql.query:
query SearchRepositories($query: String!, $before: String) {
    search(first: 20, query: $query, type: REPOSITORY, before: $before) {
        repositoryCount
        pageInfo {
            endCursor
            hasNextPage
        }
        nodes {
            ... on Repository {
                name
                nameWithOwner
                description
                owner {
                    avatarUrl
                    ... on Organization {
                        description
                    }
                }
                updatedAt
                viewerHasStarred
                primaryLanguage {
                    name
                    color
                }
                stargazers {
                    totalCount
                }
            }
        }
    }
}

query SearchUsers($query: String!, $before: String) {
    search(first: 20, query: $query, type: USER, before: $before) {
        nodes {
            ... on User {
                name
                login
                avatarUrl
                viewerCanFollow
                viewerIsFollowing
                followers {
                    totalCount
                }
                repositories {
                    totalCount
                }
            }
        }
        pageInfo {
            endCursor
            hasNextPage
        }
        userCount
    }
}

query Repository($owner: String!, $name: String!, $qualifiedName: String!) {
    repository(owner: $owner, name: $name) {
        name
        nameWithOwner
        description
        url
        homepageUrl
        createdAt
        updatedAt
        viewerHasStarred
        diskUsage
        isFork
        parent {
            nameWithOwner
        }
        owner {
            login
            avatarUrl
            ... on Organization {
                description
            }
        }
        primaryLanguage {
            name
            color
        }
        languages(first: 20) {
            totalCount
            totalSize
            edges {
                size
                node {
                    name
                    color
                }
            }
        }
        licenseInfo {
            name
        }
        defaultBranchRef {
            name
        }
        stargazers {
            totalCount
        }
        forks {
            totalCount
        }
        watchers {
            totalCount
        }
        mentionableUsers {
            totalCount
        }
        issues(states: [OPEN]) {
            totalCount
        }
        pullRequests(states: [OPEN]) {
            totalCount
        }
        releases {
            totalCount
        }
        ref(qualifiedName: $qualifiedName) {
            target {
                ... on Commit {
                    history {
                        totalCount
                    }
                }
            }
        }
    }
}

query Viewer {
    viewer {
        id
        name
        login
        avatarUrl
        url
        websiteUrl
        bio
        company
        email
        location
        createdAt
        updatedAt
        viewerCanFollow
        viewerIsFollowing
        isViewer
        followers {
            totalCount
        }
        following {
            totalCount
        }
        starredRepositories {
            totalCount
        }
        issues {
            totalCount
        }
        repositories {
            totalCount
        }
        watching {
            totalCount
        }
        contributionsCollection {
            totalRepositoryContributions
        }
        pinnedItems(first: 6) {
            nodes {
              ... on Repository {
                name
                nameWithOwner
                description
                owner {
                  avatarUrl
                }
                updatedAt
                viewerHasStarred
                primaryLanguage {
                  name
                  color
                }
                stargazers {
                  totalCount
                }
              }
            }
        }
        organizations(first: 10) {
            nodes {
                name
                login
                avatarUrl
                ... on Organization {
                    description
                }
            }
        }
    }
}

query User($login: String!) {
    user(login: $login) {
        name
        login
        avatarUrl
        url
        websiteUrl
        bio
        company
        email
        location
        createdAt
        updatedAt
        viewerCanFollow
        viewerIsFollowing
        isViewer
        followers {
            totalCount
        }
        following {
            totalCount
        }
        starredRepositories {
            totalCount
        }
        issues {
            totalCount
        }
        repositories {
            totalCount
        }
        watching {
            totalCount
        }
        pinnedItems(first: 6) {
            nodes {
              ... on Repository {
                name
                nameWithOwner
                description
                owner {
                  avatarUrl
                }
                updatedAt
                viewerHasStarred
                primaryLanguage {
                  name
                  color
                }
                stargazers {
                  totalCount
                }
              }
            }
        }
        organizations(first: 10) {
            nodes {
                name
                login
                avatarUrl
                ... on Organization {
                    description
                }
            }
        }
    }
}
